import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Layout from '../components/Layout';
import { fetchPaymentHistory } from '../services/api';

function PaymentHistoryPage() {
  const [history, setHistory] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  // Form fields
  const [residentName, setResidentName] = useState('');
  const [address, setAddress] = useState('');
  const [date, setDate] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    fetchPaymentHistory()
      .then(data => {
        setHistory(data);
        setIsLoading(false);
      })
      .catch(error => {
        console.error("Error fetching payment history:", error);
        setIsLoading(false);
      });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (paymentMethod === 'card') {
      // Navigate to Card Payment Page
      navigate('/add-card');
    } else if (paymentMethod === 'cash') {
      alert(`Cash payment submitted for ${residentName} on ${date}.`);
      // Here you can also send the data to backend if needed
      setResidentName('');
      setAddress('');
      setDate('');
      setPaymentMethod('');
    } else {
      alert('Please select a payment method.');
    }
  };

  if (isLoading)
    return (
      <Layout activeTab="Payment History" variant="sidebar">
        <p className="p-8 text-lg">Loading Payment History...</p>
      </Layout>
    );

  return (
    <Layout activeTab="Payment History" variant="sidebar">
      <div className="max-w-3xl mx-auto p-8">
        <h1 className="text-3xl font-bold mb-2 text-gray-800">Payment & Payback History</h1>
        <p className="text-gray-500 mb-8">Track your charges and recycling credits/paybacks.</p>

        {/* New Payment Form */}
        <div className="bg-white rounded-xl shadow p-6 mb-10 border border-gray-100">
          <h2 className="text-xl font-semibold mb-4 text-gray-800">Make a Payment</h2>
          <form onSubmit={handleSubmit} className="grid grid-cols-1 sm:grid-cols-2 gap-4">
            <div className="sm:col-span-2">
              <label className="block text-sm font-medium text-gray-700">Resident Name</label>
              <input
                className="mt-1 w-full border rounded-md px-3 py-2"
                value={residentName}
                onChange={(e) => setResidentName(e.target.value)}
                placeholder="Enter resident name"
                required
              />
            </div>
            <div className="sm:col-span-2">
              <label className="block text-sm font-medium text-gray-700">Address</label>
              <input
                className="mt-1 w-full border rounded-md px-3 py-2"
                value={address}
                onChange={(e) => setAddress(e.target.value)}
                placeholder="123 Main St, City"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Date</label>
              <input
                type="date"
                className="mt-1 w-full border rounded-md px-3 py-2"
                value={date}
                onChange={(e) => setDate(e.target.value)}
                required
              />
            </div>

            {/* Payment Method Selection */}
            <div className="sm:col-span-2">
              <label className="block text-sm font-medium text-gray-700 mb-2">Payment Method</label>
              <div className="flex items-center gap-6">
                <label className="flex items-center gap-2">
                  <input
                    type="radio"
                    name="paymentMethod"
                    value="card"
                    checked={paymentMethod === 'card'}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                  />
                  Card Payment
                </label>
                <label className="flex items-center gap-2">
                  <input
                    type="radio"
                    name="paymentMethod"
                    value="cash"
                    checked={paymentMethod === 'cash'}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                  />
                  Cash Payment
                </label>
              </div>
            </div>

            <div className="sm:col-span-2">
              <button
                type="submit"
                className="inline-flex items-center gap-2 px-5 py-2 rounded-md text-white bg-emerald-600 hover:bg-emerald-700"
              >
                Submit
              </button>
            </div>
          </form>
        </div>

        {/* Payment History */}
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 mb-10">
          {history.length > 0 ? history.map((record, idx) => (
            <div key={idx} className={`rounded-xl shadow-lg p-6 flex flex-col gap-2 ${record.type === 'Payback' ? 'bg-green-50' : 'bg-white'}`}>
              <div className="flex items-center justify-between">
                <span className="text-lg font-semibold text-gray-700">{record.type}</span>
                <span className={`px-3 py-1 rounded-full text-xs font-bold ${record.status === 'Paid' ? 'bg-blue-100 text-blue-700' : 'bg-yellow-100 text-yellow-700'}`}>{record.status}</span>
              </div>
              <div className="text-2xl font-bold text-gray-900">
                {record.type === 'Payback' ? `+${record.amount.toFixed(2)}` : `-${record.amount.toFixed(2)}`} <span className="text-base font-normal text-gray-600">USD</span>
              </div>
              <div className="text-sm text-gray-500">{record.date}</div>
            </div>
          )) : (
            <div className="col-span-2 p-8 text-center text-gray-500 bg-white rounded-xl shadow">No financial records found.</div>
          )}
        </div>
      </div>
    </Layout>
  );
}

export default PaymentHistoryPage;
