import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

// Dummy icons, replace with a real icon library!
const TrashIcon = (props) => <svg {...props} fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" /></svg>;
const ClockIcon = (props) => <svg {...props} fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" /></svg>;
const CalendarIcon = (props) => <svg {...props} fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" /></svg>;

const KPICard = ({ title, value, unit, icon: Icon, color }) => (
  <div className={`rounded-2xl shadow-xl px-6 py-8 bg-${color}-700 text-white flex flex-col justify-between`}>
    <div className="flex justify-between items-center mb-2">
      <span className="text-lg font-semibold">{title}</span>
      <Icon className="w-9 h-9 opacity-80" />
    </div>
    <span className="text-4xl font-bold mt-2">{value} <span className="text-base font-normal">{unit}</span></span>
  </div>
);

const StatusTag = ({ status }) => {
  let colors = {
    completed:  "bg-green-200 text-green-800",
    scheduled:  "bg-blue-200 text-blue-800",
    'pending payment': "bg-yellow-200 text-yellow-800"
  };
  const tagClass = colors[status.toLowerCase()] || "bg-gray-200 text-gray-700";
  return (
    <span className={`px-3 py-1 text-xs rounded-full font-semibold ${tagClass}`}>
      {status}
    </span>
  );
};

// Placeholder analytics bar
const AnalyticsBar = ({ percent, day }) => (
  <div className="flex flex-col w-14">
    <div className="h-20 bg-green-600 rounded-xl mb-2" style={{ height: `${percent}px` }} />
    <span className="text-xs text-center text-gray-500">{day}</span>
  </div>
);

function Dashboard() {
  const navigate = useNavigate();
  const [data, setData] = useState(null);
  const [isRequesting, setIsRequesting] = useState(false);

  useEffect(() => {
    // Replace with your own API call!
    setData({
      totalWaste: 150.5,
      nextPickupDate: '2025-10-25',
      pendingPickups: 2,
      activityReport: [
        { day: 'Mon', percent: 90 },
        { day: 'Tue', percent: 60 },
        { day: 'Wed', percent: 74 },
        { day: 'Thu', percent: 40 },
        { day: 'Fri', percent: 50 },
        { day: 'Sat', percent: 30 },
      ],
      recentActivity: [
        { id: 1, date: 'Oct 10, 2025', type: 'Mixed', weight: 4.2, status: 'Completed' },
        { id: 2, date: 'Oct 15, 2025', type: 'Recycling', weight: 0, status: 'Scheduled' },
        { id: 3, date: 'Sep 28, 2025', type: 'Large Item', weight: 15, status: 'Pending Payment' }
      ]
    });
  }, []);

  if (!data) return <div className="p-8 text-lg">Loading dashboard data...</div>;

  const { totalWaste, nextPickupDate, pendingPickups, activityReport, recentActivity } = data;

  return (
    <div className="min-h-screen bg-gray-50 flex">
      {/* Sidebar */}
      <div className="w-60 bg-white shadow-xl flex flex-col justify-between py-8 px-6 rounded-r-3xl">
        <div>
          <div className="text-2xl font-bold text-green-700 mb-10">WastePro</div>
          <nav className="flex flex-col gap-6">
            <a className="font-bold text-green-700 px-2 py-2 rounded-lg bg-green-100" href="/">Dashboard</a>
            <a className="text-gray-700 hover:bg-gray-100 px-2 py-2 rounded-lg" href="/stats">Statistics</a>
            <a className="text-gray-700 hover:bg-gray-100 px-2 py-2 rounded-lg" href="/history">History</a>
            <a className="text-gray-700 hover:bg-gray-100 px-2 py-2 rounded-lg" href="/payment">Payment</a>
            <a className="text-gray-700 hover:bg-gray-100 px-2 py-2 rounded-lg" href="/settings">Settings</a>
          </nav>
        </div>
        <button className="mt-10 text-gray-500 hover:text-red-600">Logout</button>
      </div>

      {/* Main Content */}
      <div className="flex-1 px-10 py-10">
        {/* Top Bar */}
        <div className="flex justify-between items-center mb-10">
          <h1 className="text-3xl font-bold text-gray-800">Dashboard</h1>
          <button
            onClick={() => setIsRequesting(true)}
            className="bg-green-700 hover:bg-green-800 text-white font-bold py-3 px-6 rounded-xl shadow-lg transition duration-200 flex items-center gap-2"
          >
            <TrashIcon className="w-6 h-6" />
            <span>Request Pickup</span>
          </button>
        </div>

        {/* KPI Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-10">
          <KPICard
            title="Total Waste Collected"
            value={totalWaste}
            unit="kg"
            icon={TrashIcon}
            color="green"
          />
          <KPICard
            title="Next Scheduled Pickup"
            value={nextPickupDate ? new Date(nextPickupDate).toLocaleDateString() : 'N/A'}
            unit=""
            icon={CalendarIcon}
            color="green"
          />
          <KPICard
            title="Pending Pickups"
            value={pendingPickups}
            unit="requests"
            icon={ClockIcon}
            color="green"
          />
        </div>

        {/* Analytics Section */}
        <div className="bg-white rounded-2xl shadow-xl px-8 py-6 mb-10">
          <div className="text-xl font-semibold text-gray-800 mb-4">Pickup Analytics</div>
          <div className="flex gap-6 items-end">
            {activityReport.map(({ day, percent }, i) => (
              <AnalyticsBar key={i} percent={percent} day={day} />
            ))}
          </div>
        </div>

        {/* Recent Activity Table */}
        <div className="bg-white px-8 py-6 rounded-2xl shadow-xl">
          <div className="text-xl font-semibold text-gray-800 mb-4">Recent Pickup Activity</div>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-100">
              <thead>
                <tr>
                  <th className="py-3 text-left text-xs font-medium text-gray-500">Date</th>
                  <th className="py-3 text-left text-xs font-medium text-gray-500">Type</th>
                  <th className="py-3 text-left text-xs font-medium text-gray-500">Weight (kg)</th>
                  <th className="py-3 text-left text-xs font-medium text-gray-500">Status</th>
                  <th />
                </tr>
              </thead>
              <tbody>
                {recentActivity && recentActivity.length > 0 ? (
                  recentActivity.map(activity => (
                    <tr key={activity.id} className="hover:bg-gray-50 transition">
                      <td className="py-4 whitespace-nowrap text-sm text-gray-900">{activity.date}</td>
                      <td className="py-4 whitespace-nowrap text-sm text-gray-700">{activity.type}</td>
                      <td className="py-4 whitespace-nowrap text-sm text-gray-700">{activity.weight > 0 ? activity.weight : 'N/A'}</td>
                      <td className="py-4 whitespace-nowrap">
                        <StatusTag status={activity.status} />
                      </td>
                      <td className="py-4 whitespace-nowrap text-right text-sm font-medium">
                        <a href="#" className="text-green-700 hover:text-green-900">View</a>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="5" className="py-8 text-center text-gray-400">
                      No recent pickup activity. Click 'Request Pickup' to get started!
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Request Pickup Modal (pseudo-code, style with your modal/side-panel component) */}
      {isRequesting && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-end z-50">
          <div className="w-96 bg-white p-8 rounded-l-3xl shadow-2xl">
            <div className="flex justify-between items-center mb-6">
              <div className="text-xl font-semibold text-gray-900">Request Pickup</div>
              <button className="text-gray-400 hover:text-red-600" onClick={() => setIsRequesting(false)}>
                ×
              </button>
            </div>
            {/* Your form goes here */}
            <button
              className="bg-green-700 text-white py-2 px-4 rounded-lg w-full mt-6"
              onClick={() => setIsRequesting(false)}
            >
              Confirm Request
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
