// services/residentApi.js

const BASE_URL = "http://localhost:500/api";

// Fetch dashboard data
export async function fetchDashboardData() {
    const token = localStorage.getItem("token");
    const res = await fetch(`${BASE_URL}/pickups`, {
        headers: {
            "Authorization": `Bearer ${token}`,
            "Content-Type": "application/json",
        },
    });
    if (!res.ok) throw new Error("Failed to fetch dashboard data");
    return await res.json();
}

// Request new pickup
export async function requestPickup(formData) {
    const token = localStorage.getItem("token");
    const res = await fetch(`${BASE_URL}/pickups`, {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
        },
        body: JSON.stringify(formData),
    });

    if (!res.ok) {
        const errorData = await res.json().catch(() => ({}));
        throw new Error(errorData.message || "Failed to request pickup");
    }

    return await res.json();
}
