import React from 'react';

const RequestPickup = ({ onClose, onConfirm }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    // Get form data
    const formData = {
      date: e.target.date.value,
      wasteType: e.target.wasteType.value
    };
    onConfirm(formData);
  };

  return (
    <>
      {/* Modal Backdrop */}
      <div className="fixed inset-0 bg-black bg-opacity-40 z-40 transition-opacity" onClick={onClose}></div>
      {/* Modal Panel */}
      <div className="fixed right-0 top-0 h-full w-full max-w-md bg-white shadow-2xl z-50 flex flex-col p-8 animate-slide-in">
        <div className="flex justify-between items-center mb-6">
          <h3 className="text-2xl font-bold text-gray-800">Request Pickup</h3>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-700 text-2xl">&times;</button>
        </div>
        <form onSubmit={handleSubmit} className="flex flex-col gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Pickup Date</label>
            <input name="date" type="date" className="w-full border rounded-lg px-3 py-2" required />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Waste Type</label>
            <select name="wasteType" className="w-full border rounded-lg px-3 py-2" required>
              <option>General</option>
              <option>Recycling</option>
              <option>Large Item</option>
            </select>
          </div>
          <button type="submit" className="bg-purple-600 hover:bg-purple-700 text-white font-semibold py-3 px-6 rounded-xl shadow-md transition duration-200">Confirm Request</button>
        </form>
      </div>
      <style>{`
        @keyframes slide-in {
          from { transform: translateX(100%); }
          to { transform: translateX(0); }
        }
        .animate-slide-in {
          animation: slide-in 0.3s cubic-bezier(.4,0,.2,1);
        }
      `}</style>
    </>
  );
};

export default RequestPickup;
