// services/residentApi.js

const BASE_URL = "http://localhost:5000/api";

// Fetch dashboard data
export const fetchDashboardData = async () => {
    const token = localStorage.getItem("token"); // if you use auth
    const res = await fetch(`${BASE_URL}/pickups`, {
        headers: {
            "Authorization": token ? `Bearer ${token}` : "",
            "Content-Type": "application/json",
        },
    });
    if (!res.ok) throw new Error("Failed to fetch dashboard data");
    return await res.json();
};

// Request new pickup
export const requestPickup = async (formData) => {
    const token = localStorage.getItem("token"); // if auth needed
    const res = await fetch(`${BASE_URL}/pickups`, {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
            "Authorization": token ? `Bearer ${token}` : "",
        },
        body: JSON.stringify(formData),
    });

    if (!res.ok) {
        const errorData = await res.json().catch(() => ({}));
        throw new Error(errorData.message || "Failed to schedule pickup");
    }

    return await res.json();
};
