import React, { useState, useEffect } from 'react';
import Layout from '../components/Layout';
import { fetchPaymentHistory } from '../services/api';

function PaymentHistoryPage() {
    const [history, setHistory] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        fetchPaymentHistory()
            .then(data => {
                setHistory(data);
                setIsLoading(false);
            })
            .catch(error => {
                console.error("Error fetching payment history:", error);
                setIsLoading(false);
            });
    }, []);

    if (isLoading) return <Layout activeTab="Payment History"><p>Loading Payment History...</p></Layout>;

    return (
        <Layout activeTab="Payment History">
            <h1>Payment & Payback History</h1>
            <p>Track your charges and recycling credits/paybacks.</p>

            <table style={styles.table}>
                <thead>
                    <tr>
                        <th style={styles.th}>Date</th>
                        <th style={styles.th}>Type</th>
                        <th style={styles.th}>Amount</th>
                        <th style={styles.th}>Status</th>
                    </tr>
                </thead>
                <tbody>
                    {history.length > 0 ? history.map((record, index) => (
                        <tr key={index} style={record.type === 'Payback' ? {backgroundColor: '#e8f5e9', fontWeight: 'bold'} : {}}>
                            <td style={styles.td}>{record.date}</td>
                            <td style={{...styles.td, color: record.type === 'Payback' ? '#388e3c' : '#333'}}>
                                {record.type} {record.type === 'Payback' && ' (Recycling Credit)'}
                            </td>
                            <td style={styles.td}>
                                {record.type === 'Payback' ? `+${record.amount.toFixed(2)}` : `-${record.amount.toFixed(2)}`}
                            </td>
                            <td style={styles.td}>{record.status}</td>
                        </tr>
                    )) : (
                        <tr><td colSpan="4" style={styles.td}>No financial records found.</td></tr>
                    )}
                </tbody>
            </table>
        </Layout>
    );
}

const styles = {
    table: { width: '100%', borderCollapse: 'collapse', marginTop: '20px' },
    th: { border: '1px solid #ddd', padding: '10px', textAlign: 'left', backgroundColor: '#f2f2f2' },
    td: { border: '1px solid #ddd', padding: '10px', textAlign: 'left' }
};

export default PaymentHistoryPage;
