import React, { useState, useEffect } from 'react';
import Layout from '../components/Layout';
import RequestPickup from '../components/RequestPickup';
// TODO: Implement fetchDashboardData and requestPickup in services/residentApi
import { fetchDashboardData, requestPickup } from '../services/residentApi';

function Dashboard() {
    const [data, setData] = useState({});
    const [isRequesting, setIsRequesting] = useState(false);
    
    useEffect(() => {
        fetchDashboardData().then(setData).catch(() => {
            // Redirect on error/no auth
            window.location.href = '/';
        });
    }, []);

    const handleConfirmRequest = async (formData) => {
        const result = await requestPickup(formData);
        
        if (result.suggestion) {
            alert(`No slots available on that day. System suggests: ${result.suggestion}`);
        } else if (result.requiresPayment) {
            window.location.href = '/resident/payment';
        } else {
            alert('Pickup scheduled successfully!');
            fetchDashboardData().then(setData); 
        }
    };

    if (!data.totalWaste) return <p>Loading...</p>;

    return (
        <Layout activeTab="Home">
            <h2>Dashboard</h2>
            {/* Display Metrics from data */}
            <p>Total Waste Collected: {data.totalWaste} kg</p>
            {/* ... Other metrics ... */}

            <button onClick={() => setIsRequesting(true)}>+ Request Pickup</button>
            
            {/* Recent Activity Table */}
            {/* ... */}
            
            {/* Request Pickup Side Panel */}
            {isRequesting && (
                <RequestPickup 
                    onClose={() => setIsRequesting(false)} 
                    onConfirm={handleConfirmRequest}
                />
            )}
        </Layout>
    );
}
export default Dashboard;
